import{_ as n,c as i,a as t,o as r}from"./app-DRszs4YQ.js";const a="/assets/Einzeltermine-aQn_LYAj.png",s="/assets/Button%20_Einzeltermin%20anlegen-BU5Gt6d1.png",l="/assets/Bild-BnhkYQs5.png",d="/assets/Zeitraum-B1zycrWM.png",h="/assets/Organisation-B0ng3NLD.png",g="/assets/Organisation%20wechseln-DbLAN_pv.png",u="/assets/Kalenderkategorien-BSSD5ax3.png",o="/assets/Duplizieren-NfK4uqtx.png",m="/assets/bearbeiten%20Tabellenansicht-D-cgS36V.png",c="/assets/bearbeiten%20Einzelansicht-Ca8V67i-.png",p={};function b(z,e){return r(),i("div",null,e[0]||(e[0]=[t('<p><a href="/get-started">« Zurück</a></p><h1 id="einzeltermine" tabindex="-1"><a class="header-anchor" href="#einzeltermine"><span>Einzeltermine</span></a></h1><p>Einzeltermine können Sie direkt im <strong>Smarten Gemeinschaftskalender</strong> anlegen und verwalten. Sie eignen sich besonders für <strong>einmalige Veranstaltungen</strong> oder <strong>Veranstaltungen ohne festen Rhythmus</strong>.</p><p>Im Seitenmenü der Terminverwaltung unter der Rubrik <strong>Einzeltermine</strong> können Sie:</p><ul><li>Neue Einzeltermine <strong>anlegen</strong></li><li>Termine <strong>duplizieren</strong>, um ähnliche Veranstaltungen schnell anlegen zu können</li><li>Bestehende Einzeltermine <strong>bearbeiten</strong></li><li>Den <strong>Veranstaltungsstatus aktualisieren</strong></li></ul><p><img src="'+a+'" alt="Bildschirmaufnahme der Rubrik Einzeltermine in der Terminverwaltung"></p><p>Diese Dokumentation führt Sie <strong>Schritt für Schritt</strong> durch den gesamten Prozess – von der Erstellung bis zur Aktualisierung Ihrer Einzeltermine.</p><h2 id="einzeltermine-erstellen" tabindex="-1"><a class="header-anchor" href="#einzeltermine-erstellen"><span>Einzeltermine erstellen</span></a></h2><p>Um einen neuen Einzeltermin zu erstellen, klicken Sie auf den Button <em>+ Einzeltermin anlegen</em> (oben rechts).</p><img src="'+s+'" alt="Button + Einzeltermin anlegen" style="width:200px;"><p>Es öffnet sich ein Formular. Hier können Sie nachlesen, wie Sie die Eingabefelder am besten nutzen.</p><h3 id="titel" tabindex="-1"><a class="header-anchor" href="#titel"><span>Titel</span></a></h3><p>Der Titel ist das, was potenzielle Teilnehmer*innen als erstes von Ihrer Veranstaltung sehen. Geben Sie daher einen aussagekräftigen Namen an, der Thema und Art der Veranstaltung deutlich macht.</p><p><strong>Hinweis:</strong> Halten Sie den Titel so kurz wie möglich, aber so lang wie nötig, damit er in Übersichten gut lesbar bleibt und gleichzeitig Interesse weckt.</p><h3 id="bild" tabindex="-1"><a class="header-anchor" href="#bild"><span>Bild</span></a></h3><p>Ein Veranstaltungsbild kann die Art und den Charakter Ihrer Veranstaltung <strong>visuell hervorheben</strong>. Es wird sowohl als Vorschaubild auf der Kalenderstartseite und in den Suchergebnissen als auch auf der Detailseite der jeweiligen Veranstaltung angezeigt.<br> Wenn Sie kein Bild hochladen, wird automatisch der <strong>Standard-Farbverlauf des Smarten Gemeinschaftskalenders</strong> hinterlegt.</p><p>Klicken Sie auf das Feld <em>Datei auswählen</em>, um in Ihren Ordnern das gewünschte Bild auszuwählen, oder ziehen Sie es per <strong>Drag &amp; Drop</strong> direkt in das Eingabefeld.</p><p><strong>Empfehlungen für das Bild:</strong></p><ul><li>Wählen Sie ein <strong>aussagekräftiges Motiv</strong> im <strong>Querformat</strong>.</li><li>Verzichten Sie auf kleine Schriften, da diese insbesondere in der Vorschau schlecht lesbar sind.</li><li>Verwenden Sie das Bild nicht für Detailinformationen – diese gehören in die Veranstaltungsbeschreibung.</li></ul><p>Nach dem Hochladen erscheint eine <strong>Vorschau</strong> rechts neben dem Eingabefeld.</p><ul><li>Um das Bild zu ersetzen, laden Sie einfach ein neues hoch.</li><li>Mit dem <strong>Löschen-Button</strong> in der Vorschau können Sie das aktuelle Bild jederzeit entfernen.</li></ul><p><img src="'+l+'" alt="Beispiel eines hochgeladenen Bildes mit Pfeil, welcher den Löschen-Button anzeigt"></p><h3 id="beschreibung" tabindex="-1"><a class="header-anchor" href="#beschreibung"><span>Beschreibung</span></a></h3><p>Fügen Sie hier detaillierte Informationen zu Ihrem Termin hinzu, die für Teilnehmende wichtig sind, z. B.:</p><ul><li>Anmeldeprozess (ist eine Anmeldung nötig und Preise)</li><li>Ablauf und Inhalte</li><li>Zielgruppe (Alter, Vorkenntnisse)</li><li>Voraussetzungen oder mitzubringende Ausstattungen</li><li>weitere veranstaltungsspezifische Details</li></ul><p><strong>Hinweis:</strong> Halten Sie die Beschreibung übersichtlich strukturiert und leicht verständlich, damit Sie den Teilnehmer*innen hilft, sich ein genaues Bild vom Termin zu machen.</p><h3 id="zeitraum-festlegen" tabindex="-1"><a class="header-anchor" href="#zeitraum-festlegen"><span>Zeitraum festlegen</span></a></h3><p>Legen Sie fest, <strong>wann</strong> und <strong>wie lange</strong> Ihr Einzeltermin stattfindet.</p><img src="'+d+'" alt="Zeitraum festlegen: Datum, Beginn und Dauer" style="width:400px;"><p><strong>Startdatum:</strong> Wählen Sie den Tag, an dem der Termin stattfinden soll.</p><p><strong>Beginn (Uhrzeit):</strong> Geben Sie an, zu welcher Uhrzeit der Termin startet. Im ersten Feld geben Sie die Stunde (00 - 23) und im zweiten Feld die Minuten (00-59) an.</p><p><strong>Dauer:</strong> Ausgehend von dem Startzeitpunkt legen Sie mit der Dauer fest, wie lange Ihre Veranstaltung stattfindet. Geben Sie bitte die Anzahl der Stunden und Minuten an und <strong>keine Uhrzeit</strong>.</p><h3 id="organisation" tabindex="-1"><a class="header-anchor" href="#organisation"><span>Organisation</span></a></h3><p>Da Sie Teil mehrerer Organisationen sein können, wird Ihnen an dieser Stelle die Organisation angezeigt, für die Sie gerade den Einzeltermin anlegen. Sie können die Organisation im Formular nicht bearbeiten – sie wird Ihnen ausgegraut angezeigt.</p><img src="'+h+'" alt="Ausgegraute Organisation im Formular" style="width:400px;"><p>Sollten Sie sich in der falschen Organisationsoberfläche befinden, können Sie diese oben im Seitenmenü unkompliziert wechseln.</p><img src="'+g+'" alt="Wechseln der Organisation im Seitenmenü" style="width:300px;"><h3 id="kategorien" tabindex="-1"><a class="header-anchor" href="#kategorien"><span>Kategorien</span></a></h3><p>Beim Anlegen Ihrer Veranstaltung können Sie eine von insgesamt 30 Kategorien wählen. Entscheiden Sie sich dabei für die Kategorie, die am ehesten zu der Art und den Inhalten der Veranstaltung passt.</p><p>Behalten Sie dabei im Hinterkopf: Menschen können sowohl in der Suche als auch beim Abonnieren von Veranstaltungsbenachrichtigungen nach Kategorien filtern. Dieser Schritt ist daher von besonderer Wichtigkeit, damit die richtigen Personen von Ihrer Veranstaltung erfahren.</p><p>Um in der Kalenderansicht die Kategorien farblich unterscheiden zu können, werden die 30 Kategorien zu 5 Oberkategorien zusammengefasst. Ihre Veranstaltung wird entsprechend automatisch zugeordnet.</p><img src="'+u+'" alt="5 Oberkategorien der Kalenderansicht: Kultur &amp; Kreatives, Gemeinschaft &amp; Engagement, Familie &amp; Freizeit, Gesundheit &amp; Bewegung, Wirtschaft &amp; Bildung" style="width:200px;"><p>Konkret setzen sich die Kategorien folgendermaßen zusammen:</p><ul><li><strong>Kultur &amp; Kreatives:</strong> Darstellende &amp; bildende Kunst, Film &amp; Medien, Theater, Sprache &amp; Kultur, Fotografie, Musik, Kunsthandwerk, Kabarett, Kunst</li><li><strong>Gemeinschaft &amp; Engagement:</strong> Ehrenamt, LGBTQIA+, Netzwerke, Gesellschaft &amp; Politik, “Glauben, Religion &amp; Spiritualität”, Lesekreise, Treffen, Benefiz &amp; Spendenaktionen, Familie, Gemeinschaft &amp; Ehrenamt</li><li><strong>Familie &amp; Freizeit:</strong> Natur &amp; Abenteuer, Tiere &amp; Haustiere, Tanzen &amp; Feiern, Mode &amp; Schönheit, Spiele &amp; Gaming, “Autos, Boote, Luftfahrt”, Essen &amp; Trinken</li><li><strong>Gesundheit &amp; Bewegung:</strong> Gesundheit, Sport</li><li><strong>Wirtschaft &amp; Bildung:</strong> Wissenschaft &amp; Technologie, Bildung, Wirtschaft &amp; Unternehmen</li></ul><h3 id="beitrittsoptionen" tabindex="-1"><a class="header-anchor" href="#beitrittsoptionen"><span>Beitrittsoptionen</span></a></h3><p>Bei den Beitrittsoptionen haben Sie zwei Auswahlmöglichkeiten:</p><ol><li>Kostenlos</li><li>Extern</li></ol><h3 id="sprache" tabindex="-1"><a class="header-anchor" href="#sprache"><span>Sprache</span></a></h3><p>Wählen Sie die Sprache aus, mit der die Veranstaltung primär durchgeführt wird.</p><h3 id="status" tabindex="-1"><a class="header-anchor" href="#status"><span>Status</span></a></h3><p>Sie haben die Möglichkeit zwischen drei Statusoptionen zu wählen:</p><p><strong>1. Bestätigt:</strong> Mit dieser Standardoption veröffentlichen Sie einen Termin, welcher gesichert zu dem festgelegten Termin stattfinden soll.</p><p><strong>2. Vorläufig:</strong> Wenn Sie eine Veranstaltung bereits bekannt geben möchten, aber nicht gesichert ist, dass diese stattfinden kann oder beispielsweise der Zeitpunkt unklar ist, vergeben Sie den Status <em>Vorläufig</em>. Schreiben Sie am besten in der Beschreibung etwas zu dem <em>Grund für den Vorläufig-Status</em>!</p><p><strong>3. Abgesagt:</strong> Dieser Status wird gewählt, wenn eine Veranstaltung abgesagt werden muss. In der Regel spielt diese Option beim Anlegen einer Veranstaltung keine Rolle. Findet eine Veranstaltung nicht mehr statt, kann der Status jederzeit im Nachhinein angepasst werden.</p><h3 id="webseite-verlinken" tabindex="-1"><a class="header-anchor" href="#webseite-verlinken"><span>Webseite verlinken</span></a></h3><p>Wenn Sie eine Webseite mit weiteren relevanten Veranstaltungsinformationen bereitstellen möchten oder auf eine externe Anmeldeplattform verlinken möchten, können Sie hier eine URL (Link zur Webseite) eingeben.</p><h3 id="adresse-und-kartenansicht" tabindex="-1"><a class="header-anchor" href="#adresse-und-kartenansicht"><span>Adresse und Kartenansicht</span></a></h3><p>Im Feld <em>Adresse</em> geben Sie den Veranstaltungsort ein: <em>Straße, Hausnummer, Postleitzahl und Stadt</em>.</p><p>Die Karte aktualisiert sich automatisch entsprechend der angegebenen Adresse. Interessierte und Teilnehmer*innen können auf der Veranstaltungsseite zusätzlich zur Adresse die Kartenansicht zur Orientierung nutzen.</p><p>Steht der Veranstaltungsort noch nicht fest, kann dieses Feld leer gelassen werden. In diesem Fall empfiehlt sich ein Hinweis in der Beschreibung, dass diese Information folgen wird.</p><p>Auch bei Online-Veranstaltungen können Sie dieses Feld einfach leer lassen. Erläutern Sie ebenfalls in der Beschreibung, wie an der Veranstaltung teilgenommen werden kann – beispielsweise über einen Link zu einer Online-Konferenz.</p><h2 id="einzeltermine-duplizieren" tabindex="-1"><a class="header-anchor" href="#einzeltermine-duplizieren"><span>Einzeltermine duplizieren</span></a></h2><p>Wenn Sie bereits Einzeltermine angelegt haben und einen ähnlichen weiteren Termin anlegen wollen, haben Sie die Möglichkeit, einen bestehenden Termin zu duplizieren.</p><p>In der Rubrik Einzeltermine verwalten finden Sie eine Übersicht aller angelegten Einzeltermine. Suchen Sie die Veranstaltung, die Sie als Vorlage nutzen möchten und klicken in der jeweiligen Zeile den Button mit dem <em>Duplizieren-Symbol</em>.</p><p><img src="'+o+'" alt="Bildschirmaufnahme der Rubrik Einzeltermine in der Terminverwaltung mit Hovereffekt auf dem Duplizieren-Button in der Tabelle"></p><p>Es öffnet sich das vorausgefüllte Formular, welches Sie nach Bedarf anpassen können. Anschließend veröffentlichen Sie über den Button <em>Einzeltermin anlegen</em> Ihre Veranstaltung.</p><h2 id="einzeltermine-bearbeiten" tabindex="-1"><a class="header-anchor" href="#einzeltermine-bearbeiten"><span>Einzeltermine bearbeiten</span></a></h2><p>Sie können bestehende Einzeltermine auf zwei Wegen bearbeiten:</p><p><strong>1. Über die Terminverwaltung:</strong> Wechseln Sie in der Seitenleiste zur <em>Rubrik Einzeltermine</em>. In der dort angezeigten Tabelle finden Sie eine Übersicht aller angelegten Einzeltermine. Um einen Termin zu bearbeiten, klicken Sie einfach auf das Stift-Symbol in der entsprechenden Tabellenzeile.</p><p><img src="'+m+'" alt="Bildschirmaufnahme der Übersichtstabelle der Einzelveranstaltungen in der Terminverwaltung"></p><p><strong>2. Direkt über die Veranstaltungsseite:</strong> Wenn Sie in Ihrem Benutzerkonto eingeloggt sind, können Sie eine Veranstaltung auch direkt aus der Einzelansicht eines Termins heraus bearbeiten. Klicken Sie dazu in der rechten unteren Ecke des Seiten-Headers auf das Bearbeiten-Symbol.</p><p><img src="'+c+'" alt="Bildschirmaufnahme des Headers eines Einzeltermins im eingeloggten Zustand mit Option zum Bearbeiten "></p><p>In beiden Fällen öffnet sich das bereits vorausgefüllte <em>Bearbeitungsformular</em>, in dem Sie einzelne Angaben anpassen und anschließend aktualisieren können.</p><p>Wenn Sie alle gewünschten Änderungen vorgenommen haben, speichern Sie diese über den Button am Ende des Formulars.</p><h3 id="status-andern" tabindex="-1"><a class="header-anchor" href="#status-andern"><span>Status ändern</span></a></h3><p><strong>Hinweis:</strong> Die Inhalte zu diesem Bereich werden zeitnah ergänzt.</p><h2 id="einzeltermin-loschen" tabindex="-1"><a class="header-anchor" href="#einzeltermin-loschen"><span>Einzeltermin löschen</span></a></h2><p><strong>Hinweis:</strong> Die Inhalte zu diesem Bereich werden zeitnah ergänzt.</p>',78)]))}const k=n(p,[["render",b]]),S=JSON.parse('{"path":"/Terminverwaltung/Einzeltermine/","title":"Einzeltermine","lang":"de-DE","frontmatter":{},"headers":[{"level":2,"title":"Einzeltermine erstellen","slug":"einzeltermine-erstellen","link":"#einzeltermine-erstellen","children":[{"level":3,"title":"Titel","slug":"titel","link":"#titel","children":[]},{"level":3,"title":"Bild","slug":"bild","link":"#bild","children":[]},{"level":3,"title":"Beschreibung","slug":"beschreibung","link":"#beschreibung","children":[]},{"level":3,"title":"Zeitraum festlegen","slug":"zeitraum-festlegen","link":"#zeitraum-festlegen","children":[]},{"level":3,"title":"Organisation","slug":"organisation","link":"#organisation","children":[]},{"level":3,"title":"Kategorien","slug":"kategorien","link":"#kategorien","children":[]},{"level":3,"title":"Beitrittsoptionen","slug":"beitrittsoptionen","link":"#beitrittsoptionen","children":[]},{"level":3,"title":"Sprache","slug":"sprache","link":"#sprache","children":[]},{"level":3,"title":"Status","slug":"status","link":"#status","children":[]},{"level":3,"title":"Webseite verlinken","slug":"webseite-verlinken","link":"#webseite-verlinken","children":[]},{"level":3,"title":"Adresse und Kartenansicht","slug":"adresse-und-kartenansicht","link":"#adresse-und-kartenansicht","children":[]}]},{"level":2,"title":"Einzeltermine duplizieren","slug":"einzeltermine-duplizieren","link":"#einzeltermine-duplizieren","children":[]},{"level":2,"title":"Einzeltermine bearbeiten","slug":"einzeltermine-bearbeiten","link":"#einzeltermine-bearbeiten","children":[{"level":3,"title":"Status ändern","slug":"status-andern","link":"#status-andern","children":[]}]},{"level":2,"title":"Einzeltermin löschen","slug":"einzeltermin-loschen","link":"#einzeltermin-loschen","children":[]}],"git":{"updatedTime":1755181683000,"contributors":[{"name":"scammo","username":"scammo","email":"sbrinkmann@54gradsoftware.de","commits":1,"url":"https://github.com/scammo"}],"changelog":[{"hash":"c18eefb06cace2fe533f8f74b9214688c129951e","time":1755181683000,"email":"sbrinkmann@54gradsoftware.de","author":"scammo","message":"init vitepress"}]},"filePathRelative":"Terminverwaltung/Einzeltermine/README.md"}');export{k as comp,S as data};
